        $specifiedTypes = $this->typeSpecifier->specifyTypesInCondition($this->scope, $expr, \TenantCloud\BetterReflection\Relocated\PHPStan\Analyser\TypeSpecifierContext::createTruthy());
        $actualResult = $this->toReadableResult($specifiedTypes);
        $this->assertSame($expectedPositiveResult, $actualResult, \sprintf('if (%s)', $this->printer->prettyPrintExpr($expr)));
        $specifiedTypes = $this->typeSpecifier->specifyTypesInCondition($this->scope, $expr, \TenantCloud\BetterReflection\Relocated\PHPStan\Analyser\TypeSpecifierContext::createFalsey());
        $actualResult = $this->toReadableResult($specifiedTypes);
